export function toggleFullScreen(elementClassName, bottomBarClassName) {
    const element = document.getElementsByClassName(elementClassName)[0];
    const isMaximized = element.classList.contains("maximized");
  
    if (isMaximized) {
      element.classList.remove("maximized");
      element.style.height = ''; // Reset height
    } else {
      element.classList.add("maximized");
  
      if (bottomBarClassName) {
        const bottomBarHeight = document.getElementsByClassName(bottomBarClassName)[0]?.offsetHeight || 0;
        element.style.height = `calc(100vh - ${bottomBarHeight}px)`; // Adjust height dynamically
      }
    }
  }
